// Wait for the ESP32 to respond with an ESC character to signify it is ready
// Parameters: None
// Returns: None
//
void wait_ESP32(void) {
	INT ch = 0; 	
	while(ch != 27) {
//		ch = getch();
		ch = keycode;
	}	
}

// Initialise the interrupts
//
void init_interrupts(void) {
	DI();
	set_vector(PORTB1_IVECT, &vblank_handler);
	EI();
}

// The main loop
//
int main(void) {
	UART 	pUART;
	UINT16 bitmapid;
	UINT16 x, y;
	UINT8 frame;
	UINT8 r,g,b;
	UINT8 mode = 0;
	UINT8 count = 0;
	UINT8 movestate = 0;
	UINT16 moveclock = 0, animationclock = 0;
	
	r = 64;
	g = 0;
	b = 0;
	
	pUART.baudRate = 384000;
	pUART.dataBits = 8;
	pUART.stopBits = 1;
	pUART.parity = PAR_NOPARITY;
	
	init_timer2(1);			// Initialise Timer 2 @ 1ms interval
	init_spi();				// Initialise SPI comms for the SD card interface
	init_UART0();			// Initialise UART0 for the ESP32 interface
	open_UART0(&pUART);		// Open the UART 
	init_interrupts();		// Initialise the interrupt vectors
	
	f_mount(&fs, "", 1);	// Mount the SD card
	
	if(coldBoot > 0) {		// If a cold boot has been detected
		wait_ESP32();		// Wait for the ESP32 to finish its bootup
	}

	//printf("AGON MOS Version %d.%02d\n\r\n\r", MOS_version, MOS_revision);
	printf("Bitmap and sprite test\n\r\n\r");
	
	
	// creating bitmaps
	for(frame = 0; frame < 8; frame++)
	{
		bitmap_sendData(frame, 16,16,clock_data[frame]);
	}
	
	bitmap_sendData(8,32,32,square_data[0]);
	bitmap_createSolidColor(9, 32,32,0xff0000ff);
	bitmap_createSolidColor(10,16,16,0xff00ff00);

	
	
	// drawing bitmaps
	x = 50;
	y = 32;
	for(frame = 0; frame < 8; frame++)
	{
		bitmap_draw(frame,x,y);
		
		bitmap_draw(8, x+100,y);
		bitmap_draw(9, x+200,y);
		bitmap_draw(10,x+300,y);
		
		y += 40;
	}
	
	// sprites def
	sprite_select(0);
	sprite_clearFrames(0);
	
	//sprite_addFrame_selected(0);
	for(frame = 0; frame < 8; frame++) sprite_addFrame(0,frame);

	sprite_moveTo(50,50);
	sprite_show();

	// Activate one sprite
	sprite_activateTotal(1);

	//while(1);
	
	
	x = 50;
	y = 50;
	moveclock = 0;
	animationclock = 0;
	movestate = 0;
	
	while(1)
	{
		if(moveclock > 20)
		{
			switch(movestate)
			{
				case 0:
					if(x < 150) x++; else movestate = 1;
					break;
				case 1:
					if(y < 100) y++; else movestate = 2;
					break;
				case 2:
					if(x > 50) x--; else movestate = 3;
					break;
				case 3:
					if(y > 50) y--; else movestate = 0;
					break;
				default:
					movestate = 0;
					break;
			}
			sprite_moveTo(x, y);
			moveclock = 0;
		}
		else moveclock++;
		
		if(animationclock > 250)
		{
			sprite_previousFrame();
			animationclock = 0;
		}
		else animationclock++;
		
		delayms(1);
	}
	return 0;
}